glsl4.vert:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
#if USE_INSTANCING
  layout(location = 0) in vec4 a_matWorld0;
  layout(location = 1) in vec4 a_matWorld1;
  layout(location = 2) in vec4 a_matWorld2;
  #if USE_LIGHTMAP
    layout(location = 3) in vec4 a_lightingMapUVParam;
  #endif
#elif USE_BATCHING
  layout(location = 4) in float a_dyn_batch_id;
  layout(set = 2, binding = 0) uniform CCLocalBatched {
    highp mat4 cc_matWorlds[10];
  };
#else
  layout(set = 2, binding = 0) uniform CCLocal {
    highp mat4 cc_matWorld;
    highp mat4 cc_matWorldIT;
    highp vec4 cc_lightingMapUVParam;
  };
#endif
layout(location = 5) in vec3 a_position;
layout(location = 6) in vec2 a_texCoord;
layout(location = 7) in vec4 a_color;
layout(location = 0) out vec2 v_uv;
layout(location = 1) out vec4 v_color;
layout(location = 2) out vec4 v_pos;
vec4 vert () {
  v_uv = a_texCoord;
  v_color = a_color;
  mat4 matWorld, matWorldIT;
    #if USE_INSTANCING
      matWorld = mat4(
        vec4(a_matWorld0.xyz, 0.0),
        vec4(a_matWorld1.xyz, 0.0),
        vec4(a_matWorld2.xyz, 0.0),
        vec4(a_matWorld0.w, a_matWorld1.w, a_matWorld2.w, 1.0)
      );
      matWorldIT = matWorld;
    #elif USE_BATCHING
      matWorld = cc_matWorlds[int(a_dyn_batch_id)];
      matWorldIT = matWorld;
    #else
      matWorld = cc_matWorld;
      matWorldIT = cc_matWorldIT;
    #endif
  vec4 pos = vec4(a_position, 1.0);
  pos.xz *= sin(cc_time.x * 4.0) * 0.05 + 1.0;
  v_pos = pos;
  return cc_matProj * cc_matView * matWorld * pos;
}
void main() { gl_Position = vert(); }

glsl4.frag:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
layout(location = 0) in vec2 v_uv;
layout(location = 1) in vec4 v_color;
layout(location = 2) in vec4 v_pos;
layout(set = 1, binding = 0) uniform sampler2D mainTexture;
vec4 frag () {
  vec4 col = texture(mainTexture, v_uv);
  col.xyz *= v_color.xyz;
  col.xyz = v_color.xyz * v_color.w + col.xyz * (1.0 - v_color.w);
  col.a = v_color.w * v_uv.y * v_uv.y * 0.8 + (1.0 - v_color.w) * col.a;
  float y = min(fract(v_uv.y * 6.0 + cc_time.x), 0.5);
  y = 0.5 - y;
  col.a += 0.5 * y * v_color.w;
  float h = col.a;
  col.rgb *= h;
  col.rgb += (1.0 - h);
  return col;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

